import axios from "axios";

const API = axios.create({
    baseURL: 'http://127.0.0.1:8000/api/v1',
})

document.addEventListener('DOMContentLoaded', function() {
    API.get('/get-token/').then(response => {
        console.log(response.data)
        API.defaults.headers.common['X-CSRFToken'] = response.data.token
    }).catch(err => {
        console.log(err)
    })
})
// let token = document.getElementsByName("csrfmiddlewaretoken")

// if (token) {
//     API.defaults.headers.common['X-CSRFToken'] = token[0].value
// }
// else {
//     console.error('CSRF token not found')
// }

API.interceptors.response.use(response => response, async err => {
    if (err.response.status === 401) {
        this.$store.dispatch('refreshToken', {
            refreshToken: this.$store.state.refreshToken
        }).then(() => {
            return API(err.config)
        }).catch(err => {
            console.log(err)
        })
    }
})

export { API }
