import {createRouter, createWebHistory} from 'vue-router'
import Home from './views/Home'
import Login from './views/Login'
import Logout from './views/Logout'
import Register from './views/Register'
import VariantList from './views/VariantList'
import addVariant from './views/addVariant'
import EGETask1 from './views/EGETask1'
import EGETask2 from './views/EGETask2'
import EGETask3 from './views/EGETask3'
import EGETask4 from './views/EGETask4'


//________ CARDS ________
import DeckList from './card_views/DeckList'
import Deck from './card_views/Deck'
import CreateDeck from './card_views/CreateDeck'
import CreateCard from './card_views/CreateCard'


const routes = [
    {
        path: '/',
        name: 'home',
        component: Home
    },
    {
        path: '/login',
        name: 'login',
        component: Login
    },
    {
        path: '/logout',
        name: 'logout',
        component: Logout
    },
    {
        path: '/register',
        name: 'register',
        component: Register
    },
    {
        path: '/:exam',
        name: 'exam',
        component: VariantList,
    },
    {
        path: '/:exam/add-variant',
        name: 'add-variant',
        component: addVariant,
    },
    {
        path: `/:exam/:id/1`,
        name: 'ege-task1',
        component: EGETask1,
    },
    {
        path: `/:exam/:id/2`,
        name: 'ege-task2',
        component: EGETask2,
    },
    {
        path: `/:exam/:id/3`,
        name: 'ege-task3',
        component: EGETask3,
    },
    {
        path: `/:exam/:id/4`,
        name: 'ege-task4',
        component: EGETask4,
    },


    //________ CARDS ________
    {
        path: '/cards/decks',
        name: 'decks',
        component: DeckList
    },
    {
        path: '/cards/decks/:slug',
        name: 'deck',
        component: Deck
    },
    {
        path: '/cards/decks/create-deck',
        name: 'create-deck',
        component: CreateDeck
    },
    {
        path: '/cards/decks/:slug/create-card',
        name: 'create-card',
        component: CreateCard
    },
    {
        path: '/cards/review',
        name: 'review',
        component: Review
    },
    {
        path: '/cards/my-decks',
        name: 'my-decks',
        component: MyDecks
    },
    {
        path: '/cards/tests',
        name: 'tests',
        component: Tests
    },
    {
        path: '/cards/tests/:id',
        name: 'test',
        component: Test
    },

]

const router = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes
})

export default router
