// import Vue from 'vue'
import { createStore} from 'vuex'
import { API } from './axios-api'

export default createStore({
    state: {
        // user: JSON.parse(localStorage.getItem('user')) || null,
        accessToken: JSON.parse(localStorage.getItem('access')) || null,
        refreshToken: JSON.parse(localStorage.getItem('refresh')) || null,
    },
    mutations: {
        updateStorage (state, { access, refresh }) {
            state.accessToken = access
            state.refreshToken = refresh
            console.log('access', state.accessToken)
            console.log('refresh', state.refreshToken)
        },
        refreshToken (state, { newAccess }) {
            state.accessToken = newAccess
            console.log('newAccess', state.accessToken)
        },
        destroyToken (state) {
            state.accessToken = null
            state.refreshToken = null
            state.user = null
        },
        getUser (state, user) {
            // state.user = user
            console.log(user)
        }
    },
    getters: {
        loggedIn (state) {
            return state.accessToken != null
        }
    },
    actions: {
        userLogin (context, usercredentials) {
            API.post('/token/', {
                username: usercredentials.username,
                password: usercredentials.password
            }).then(response => {
                localStorage.setItem('access', JSON.stringify(response.data.access))
                localStorage.setItem('refresh', JSON.stringify(response.data.refresh))
                context.commit('updateStorage', { 
                    access: response.data.access, 
                    refresh: response.data.refresh 
                })
                console.log(usercredentials.username)
                API.get('/users', { 
                    params: { username: usercredentials.username }
                }).then(response => {
                    console.log(response.data)
                    var arr = response.data
                    console.log(arr)
                    for (let i in arr) {
                        console.log(i)
                    }
                    localStorage.setItem('user', JSON.stringify(response.data))
                    context.commit('getUser', {
                        user: response.data
                    })
                })
            }).catch(err => {
                console.log(err)
            })
        },
        tokenRefresh (context, refreshToken) {
            API.post('/token/refresh/', {
                refresh: refreshToken,
            }).then(response => {
                localStorage.setItem('access', response.data.access)
                context.commit('refreshToken', { newAccess: response.data.access })
                // context.commit('getUser')
            }).catch(err => {
                console.log(err)
            })
        },
        userLogout (context) {
            if (context.getters.loggedIn) {
                localStorage.removeItem('user')
                localStorage.removeItem('access')
                localStorage.removeItem('refresh')
                context.commit('destroyToken')
            }
        }
    }
})
